create table mst_users (user_id integer not null auto_increment, created_at datetime(6), deleted_at datetime(6), email varchar(255), first_name varchar(255), is_active integer, is_doctor integer, last_name varchar(255), otp integer, password varchar(255), phone_no varchar(255), profile_image_text varchar(255), updated_at datetime(6), primary key (user_id)) engine=InnoDB;
create table pet_report (pet_report_id integer not null auto_increment, age integer, breed varchar(255), created_at datetime(6), disease varchar(255), image_text varchar(255), is_send integer, pet_name varchar(255), remarks varchar(255), report_text varchar(255), status integer, updated_at datetime(6), user_id integer, primary key (pet_report_id)) engine=InnoDB;
